mapper.select:
SELECT emp_id
FROM employee_tb
WHERE emp_name = #{emp_name}
  AND emp_birth_date = #{birthDate}

SELECT emp_pw
FROM employee_tb
WHERE emp_id = #{emp_id}
AND emp_name = #{emp_name}
AND emp_birth_date = #{birthDate}

SELECT et.emp_name, dt.de_name, et.emp_email, et.emp_phone_number, et.emp_address
FROM employee_tb et, department_tb dt 
WHERE et.idx_department = dt.idx_department AND idx_employee != #{param1}
