SELECT 
	COUNT(*) 
FROM reservation_tb rt
JOIN reservation_time_tb rtt 
	ON rt.idx_reservation = rtt.idx_reservation
		AND rt.idx_meeting_room = #{selectedRoomNo}
		AND rtt.rsv_date = #{selectedDate}
		AND rtt.rsv_time IN

			#{time}

SELECT mt.idx_meeting_room AS idxMeetingroom, mt.mr_people AS mrPeople, rtt.rsv_time IS NULL AS isReservation FROM meetingroom_tb mt LEFT JOIN reservation_tb rt ON rt.idx_meeting_room = mt.idx_meeting_room LEFT JOIN reservation_time_tb rtt ON rt.idx_reservation = rtt.idx_reservation AND rtt.rsv_date = #{selectDate} AND rtt.rsv_time IN
#{time}
GROUP BY mt.idx_meeting_room
